package logical_Programming;

public class Swap_2Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=10, b= 20;
		
		System.out.println("Before Swapping value :"+ a +" "+ b);
		
		int num1;
		
		num1 = a;
		a=b;
		b=num1;
		
		System.out.println("Before Swapping value :"+ a +" "+ b);
		

	}

}
